mapper.select:
	select * from ( 
    	select A.*, rownum as rn from ( 
    		select b_type,num,writer,subject,reg_date,readcount
			,filename,filesize,down,re_level,
			(select count(*) from board_comment where board_num=num) 
        	comment_count, show 
        	from board
        	where b_type=#{b_type} 
        	order by ref desc, re_step
    	) A 
	) where rn between #{start} and #{end}

select filename 
from board 
where num = #{num}

mapper.update:
update board 
set b_type=#{b_type} , writer=#{writer}, subject=#{subject}, content=#{content}
, filename=#{filename}, filesize=#{filesize}, ip=#{ip}, down=#{down}, passwd=#{passwd} 
where num=#{num}

update board 
set re_step = re_step+1 
where ref = #{ref} and re_step >= #{re_step}

update board 
set show = 'n' 
where num = #{num}

mapper.select:
select * from ( select A.*, rownum as rn from ( select b_type,num,writer,subject,reg_date,readcount ,filename,filesize,down,re_level, (select count(*) from board_comment where board_num=num) comment_count, show from board
where ${search_option} like '%'||#{keyword}||'%' and show = 'y' and b_type=#{b_type}
where (writer like '%'||#{keyword}||'%' or subject like '%'||#{keyword}||'%' or content like '%'||#{keyword}||'%') and show = 'y' and b_type=#{b_type}
order by ref desc, re_step ) A ) where rn between TO_NUMBER(#{start}) and TO_NUMBER(#{end})

select count(*) from board 

	
		where ${search_option} like '%'||#{keyword}||'%' 
		and show = 'y' and b_type = #{b_type}
	
	
		where (writer like '%'||#{keyword}||'%' or 
		subject like '%'||#{keyword}||'%' or content like '%'||#{keyword}||'%') 
		and show = 'y' and b_type = #{b_type}
